import React from 'react';
import { View, Text, TextInput, TouchableOpacity, Button, Alert } from 'react-native';
import styled, { css } from '@emotion/native';
import { SigninFormProps } from '@base/types';

export default function SigninForm({ email, password, handleEmailChange, handlePasswordChange }
    : SigninFormProps){
    return (
        <Container>
            <LocalSigninForm>
                <SigninInput 
                    placeholder={"이메일"}
                    onChangeText={handleEmailChange}
                    value={email}
                />
                <SigninInput 
                    placeholder={"비밀번호"}
                    onChangeText={handlePasswordChange}
                    value={password}
                />

               <SigninButton 
                   title="로그인"
                   onPress={() => Alert.alert('login')}
               />
            </LocalSigninForm>

            <View>
                <Text>아직 회원이 아니신가요?</Text>
                <TouchableOpacity
                    onPress={() => {}}
                >
                    <Text>회원가입</Text>
                </TouchableOpacity>                
            </View>

            <View>

            </View>
        </Container>
    )
}

const Container = styled.View`
    display:flex;
    justify-content: center;
    padding:15%;
`;

const LocalSigninForm = styled.View`
    margin:3% 0;
`;

const SigninInput = styled.TextInput`
    padding: 3%;
    background: white;
    margin-bottom: 3%;
    border: 1px solid rgba(0,0,0, 0.1);
    border-radius:10px;
`;

const SigninButton = styled.Button`
    margin: 20px 0;
`
