import React from 'react';
import { View, Text, TextInput, TouchableOpacity, Button, Alert } from 'react-native';
import styled, { css } from '@emotion/native';
import { SigninFormProps } from '@base/types';

export default function SigninForm({ email, password, handleEmailChange, handlePasswordChange }
    : SigninFormProps){
    return (
        <SigninFormContainer>
            <LocalSigninForm>
                <SigninInput 
                    placeholder={"이메일"}
                    onChangeText={handleEmailChange}
                    value={email}
                />
                <SigninInput 
                    placeholder={"비밀번호"}
                    onChangeText={handlePasswordChange}
                    value={password}
                />

               <SigninButton 
                   title="로그인"
                   onPress={() => Alert.alert('login')}
               />
            </LocalSigninForm>

            <SignupView>
                <Text>아직 회원이 아니신가요?</Text>
                <SignupRedirectLink
                    onPress={() => {Alert.alert('signup')}}
                >
                    <Text style={css`color: blue`}>회원가입</Text>
                </SignupRedirectLink>                
            </SignupView>

            <View>

            </View>
        </SigninFormContainer>
    )
}

const SigninFormContainer = styled.View`
`;

const LocalSigninForm = styled.View`
    margin:3% 0;
`;

const SigninInput = styled.TextInput`
    padding: 3%;
    background: white;
    margin-bottom: 3%;
    border: 1px solid rgba(0,0,0, 0.1);
    border-radius:10px;
`;

const SigninButton = styled.Button`
`;

const SignupView = styled.View`
    display: flex;
    flex-direction: row;
    justify-content:center;
    margin-top:5%;
`

const SignupRedirectLink = styled.TouchableOpacity`
    margin-left: 10px;
`
